package Date07_04_23;

public class P2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;
int a=1;
int b=0;
for (int i =1; i<=n; i++) {
	for(int j=1;j<=n;j++)
	{
		if(j%2==0)
			System.out.print(b+" ");
		else
			System.out.print(a+" ");
	}
	System.out.println();
	
}
	}


	}


