package Double;
// to print only two digits after point
public class D1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
double a=10.45436;
System.out.println(a);
// to print only two decimal after the point
System.out.println(String.format("%.2f",a));
// it is valid format of unicode system
char ch='\u0061';
System.out.println(ch);
	}

}
